function waitForInvite(){$.ajax({type:"GET",url:"/wait",dataType:"json",success:function(e,t,n){e[0]=="invite"?confirm("Vous avez une invitation de la part de "+e[2]+"\n\nVoulez vous l'accepter ?")==1?$.ajax({type:"POST",url:"/acceptInvite",dataType:"json",data:{id:e[1]},success:function(e,t,n){window.location="/game"}}):$.ajax({type:"POST",url:"/cancelInvite",dataType:"json",data:{id:e[1]},success:function(e,t,n){}}):e[0]=="game"&&(window.location="/game")},complete:function(e,t){setTimeout(function(){waitForInvite()},3e3)}})}$(document).ready(function(){waitForInvite()}),$(".ajax").click(function(e){e.preventDefault();var t=$(this);$.ajax({type:"GET",url:t.attr("href"),success:function(e,t,n){}})});